import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Notes from "./Notes";

function App() {
  const [noteSections, setNoteSections] = useState([<Notes key={0} />]);

  const addNoteSection = () => {
    const newKey = noteSections.length;
    setNoteSections([...noteSections, <Notes key={newKey} />]);
  };

  return (
    <div style={styles.container}>
      <Header />
      {noteSections}
      <div style={styles.centerButton}>
        <button style={styles.addButton} onClick={addNoteSection}>
          + Add Note Section
        </button>
      </div>
      <Footer />
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    minHeight: "100vh",
  },
  centerButton: {
    marginTop: "16px",
    textAlign: "center",
  },
  addButton: {
    padding: "10px 20px",
    border: "2px solid #333",
    borderRadius: "4px",
    background: "transparent",
    color: "#333",
    cursor: "pointer",
    fontSize: "16px",
  },
};

export default App;
