import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Notes from "./Notes";

function App() {
  const [noteSections, setNoteSections] = useState([]);

  const handleSaveNote = (noteData) => {
    setNoteSections([...noteSections, noteData]);
  };

  const renderNoteSections = () => {
    return noteSections.map((noteData, index) => (
      <Notes key={index} onSave={handleSaveNote} />
    ));
  };

  return (
    <div>
      <Header />
      {renderNoteSections()}
      <button onClick={() => setNoteSections([])}>Clear All Notes</button>
      <Footer />
    </div>
  );
}

export default App;


