import React from "react";

function Notes() {
  const[note,setNote]=useState("");

  const handleChange=(event)=>{
    setNote(event.target.value);
  };

  return (
    <div className="note">
      <h1>*Heading*</h1>
      <textarea
        value={note}
        onChange={handleChange}
        placeholder="Enter your notes here..."
      />
      <p>
        notes
      </p>
    </div>
  );
}

export default Notes;
